-- ----------------------------
-- Table structure for edu_message
-- ----------------------------
CREATE TABLE `edu_message` (
  `id` char(19) NOT NULL COMMENT '文章ID',
  `title` varchar(100) DEFAULT NULL COMMENT '文章标题',
  `content` text COMMENT '文章摘要',
  `gmt_create` datetime DEFAULT NULL COMMENT '文章创建时间',
  `gmt_modified` datetime DEFAULT NULL COMMENT '文章修改时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='文章信息表';

-- ----------------------------
-- Table structure for edu_question
-- ----------------------------
CREATE TABLE `edu_question` (
  `ID` char(19) NOT NULL COMMENT '主键',
  `CUS_ID` char(19) DEFAULT NULL COMMENT '创建人id',
  `TITLE` varchar(100) DEFAULT NULL COMMENT '问答标题',
  `CONTENT` text COMMENT '问答内容',
  `TYPE` int(1) DEFAULT NULL COMMENT '分类 1课程问答 2 学习分享',
  `STATUS` int(1) DEFAULT '0' COMMENT '状态 0可回复1不可回复（采纳最佳答案后改为1 ）',
  `REPLY_COUNT` int(11) DEFAULT '0' COMMENT '问答回复数量',
  `BROWSE_COUNT` int(11) DEFAULT '0' COMMENT '问答浏览次数',
  `PRAISE_COUNT` int(11) DEFAULT '0' COMMENT '问答点赞数量',
  `gmt_create` datetime DEFAULT NULL COMMENT '创建时间',
  `gmt_modified` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='问答';

-- ----------------------------
-- Table structure for edu_question_comment
-- ----------------------------
CREATE TABLE `edu_question_comment` (
  `ID` char(19) NOT NULL COMMENT '主键id',
  `CUS_ID` char(19) DEFAULT NULL COMMENT '评论人id',
  `QUESTION_ID` char(19) DEFAULT NULL COMMENT '问答id',
  `CONTENT` varchar(300) DEFAULT NULL COMMENT '评论内容',
  `IS_BEST` int(1) unsigned zerofill DEFAULT '0' COMMENT '是否最佳答案 0否1是',
  `REPLY_COUNT` int(11) DEFAULT '0' COMMENT '回复数量',
  `PRAISE_COUNT` int(11) DEFAULT '0' COMMENT '点赞数',
  `gmt_create` datetime DEFAULT NULL COMMENT '回复时间',
  `parent_id` char(19) DEFAULT '0' COMMENT '父级评论id',
  `gmt_modified` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='问答评论';

-- ----------------------------
-- Table structure for edu_question_tag
-- ----------------------------
CREATE TABLE `edu_question_tag` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
